// ESERCIZIO 1
function checkAge() {
    const myAge = 32;
    let message = '';   //la variabile deve essere let

    if (myAge < 18) {
        message = `Sei troppo giovane! Hai ${myAge} anni!`;
    } else {
        message = 'Hai più di 18 anni!';
    }
}
checkAge();


